import styles from "./styles.module.css";
import {Link} from "react-router-dom";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");  //The removeItem() method removes the specified Storage Object item.
										   //The removeItem() method belongs to the Storage Object, which can be either
										   // a localStorage object or a sessionStorrage object.
		window.location.reload();
	};
	const [inputs, setInputs] = useState();
	const id = useParams().id;
	useEffect(() => {
		const fetchHandler = async () => {
		  await axios
			.get(`http://localhost:8080/${id}`) // get all data of users to fill input fields
			.then((res) => res.data)
			.then((data) => setInputs(data.user));
		//   .then((data) => console.log(data.user));
		};
		fetchHandler();
	  }, [id]);

	const [data, setData] = useState({
		firstName: "",
		lastName: "",
		email: "",
		password: "",
	});
	const [error, setError] = useState("");

	const handleChange = ({ currentTarget: input }) => {// save data
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => { //submit data
		e.preventDefault();
		try {
			const url = "http://localhost:8080/api/register";
			const { data: res } = await axios.post(url, data);
			console.log(res.message);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};
	// console.log(inputs);
	// const x=inputs.email;

	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				<h1>FakeAuth</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					<Link to="/login" style={{textDecoration: 'none',color:'#3498DB'}}>LogOut</Link>
				</button>
			</nav>
			<div className="flex">
				<div className="left">
					<h3>Name: {inputs.firstName} {inputs.lastName}</h3>
					<h3>email: {inputs.email}</h3>
				</div>
				<div className="right">
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Add Information</h1>
						<input
							type="text"
							placeholder="First Name"
							name="firstName"
							onChange={handleChange}
							value={data.firstName}
							className={styles.input}
						/>
						<input
							type="text"
							placeholder="Last Name"
							name="lastName"
							onChange={handleChange}
							value={data.lastName}
							className={styles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							className={styles.input}
						/>
						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn}>
							Sign Up
						</button>
					</form>
				</div>
			</div>

		</div>
	);
};

export default Main;
